import React, { useContext } from 'react';
import userContext from '../context/User/UserContext';

const Profile = () => {
	const { selectedUser } = useContext(userContext);

	return (
		<div>
			{selectedUser ? (
				<div>
					<img src={selectedUser.avatar} alt="" />
				</div>
			) : (
				<hi>No user selected</hi>
			)}
		</div>
	);
};

export default Profile;
